const c=()=>{const e=window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches,t=localStorage.getItem("theme");t?document.documentElement.classList.toggle("dark",t==="dark"):document.documentElement.classList.toggle("dark",e)};c();const o=()=>{const e=window.matchMedia("(prefers-color-scheme: dark)");e.addEventListener("change",()=>{document.documentElement.classList.toggle("dark",e.matches)})};o();const a=()=>{const e=document.documentElement;e.classList.toggle("dark");const t=e.classList.contains("dark");localStorage.setItem("theme",t?"dark":"light")};document.getElementById("themeToggle")?.addEventListener("click",a);
